use Data::Dumper; 




my $day = $maxdate;
my $hr = 0;
	
open(FOUT, ">" . $root . $day . ".log") or print "Could not open " .  $root . $day . ".log" . ": $!\n";

		my $HourlyDests = GenerateDestinations();
		
