sub initsrcs{
	my $count = shift;
	my @StartingClassAs = (10);
	my @StartingClassBs = (10,20,30,40,50,60,70,80);
	for(my $i = 0; $i < $count; $i++){
		my $ip = $StartingClassAs[int(rand($#StartingClassAs))];
		$ip .= "." . $StartingClassBs[int(rand($#StartingClassBs))];
		$ip .= "." . (int(rand(245)) + 5);
		$ip .= "." . (int(rand(245)) + 5);
		push(@srcs,$ip);
	}
}




sub GenerateDestinations{
	my $destinations; 
	$destinations->[0] = "115.29.46.99";
	$destinations->[1] = "61.155.30.31";

	return $destinations;
}




sub GetPanLog{
	my $DateString = shift;
	my $Dest = shift;
	my $Src = shift;
	my $appid = $appids[int(rand(@appids))];
	my $App = $apps->[$appid]->{app};
	my $DPort = $apps->[$appid]->{dport};
	my $SPort = int(rand(40000)+10000);
	my $BytesSent = int(rand(1024*1024*1024))+4000;
	my $BytesRecvd = $BytesSent * rand(6) + 4000;
	my $Bytes = $BytesSent + $BytesRecvd;
	my $Packets = int($Bytes / (rand(500) + 65));

	$line = "TIMESTAMP 1,TIMESTAMP,FE04A135782,TRAFFIC,end,1,TIMESTAMP,SRC,DEST,SRC,DEST,Internet Traffic,,,APPLICATION,vsys1,Trust,Untrust,ethernet1/8,ethernet1/2,MyLogForwarding,TIMESTAMP,UNIQUESESSIONID,1,SPORT,DPORT,SPORT,DPORT,0x400000,tcp,allow,BYTES,BYTESSENT,BYTESRCVD,PACKETS,TIMESTAMP,1,any,0,358477769,0x0, 10.0.0.0-10.255.255.255, United States,0,8,6";



	$line =~ s/TIMESTAMP/$DateString/g;
	$line =~ s/SRC/$Src/g;
	$line =~ s/DEST/$Dest/g;
	$line =~ s/APPLICATION/$App/g;
	$line =~ s/SPORT/$SPort/g;
	$line =~ s/DPORT/$DPort/g;
	$line =~ s/BYTES/$Bytes/;
	$line =~ s/BYTESSENT/$BytesSent/;
	$line =~ s/BYTESRCVD/$BytesRecvd/;
	$line =~ s/PACKETS/$Packets/;
	my $req = int(rand(400000000)) + 1000000000;
	$line =~ s/UNIQUESESSIONID/$req/;
	return $line;

}

